import './App.css';
import Header from './components/Header/Header';
import { Contenido, BotonLink, Logo } from "../UI/Estilos";
import { Link, useLocation } from "react-router-dom";


const HeaderContenido = styled(Contenido)`
    display: flex;
    justify-content: center;
    justify-content: space-between;
`;

const HeaderLink = styled(Link)`
    display: flex;
    align-items: center;
`;


const url = useLocation()
function App() {
  return (
    <div className="App">
      <Header>
        <HeaderContenido>
                  <HeaderLink to='/'>
                      <Logo src={logo} alt="" />
                  </HeaderLink>
                  {url.pathname === '/' && <BotonLink tipo='lineas' color="#fff" to='/video' >Nuevo Video</BotonLink>}
        </HeaderContenido>
      </Header>
    </div>
  );
}

export default App;
