import styled from 'styled-components';

const Body = styled.div `
    background:rgb(214, 252, 203);
        padding: 1rem 2rem;
    color: rgba(0, 0, 0, 0.63);
    font-size: 1rem;
    text-align: left;
    border: 1px;
`;

export default Body;
