import styles from "./Banner.module.css";
import bannerImage from "./banner.png"; // Ajusta la ruta según la ubicación de tu imagen

function Banner({ img, color }) {
    return (
        <div className={styles.capa} style={{ backgroundImage: `url(${bannerImage})` }}>
            <div className={styles.gradient} style={{ background: `${color}` }}>
            </div>
        </div>
    );
}

export default Banner;
