import './App.css';
import Header from './components/Header/Header';
import { Contenido, BotonLink, Logo } from "../UI/Estilos";
import { Link, useLocation } from "react-router-dom";
import { ThemeProvider } from "styled-components";
import { temaClaro } from "./components/UI/Temas";
import { EstilosGlobales } from "./EstilosGlobales";


const HeaderContenido = styled(Contenido)`
    display: flex;
    justify-content: center;
    justify-content: space-between;
`;

const HeaderLink = styled(Link)`
    display: flex;
    align-items: center;
`;

function App() {
  return (
      <ThemeProvider theme={temaClaro}>
          <EstilosGlobales />
          <Router>
              <Cabecera />
              <Routes>
                  <Route path="/" element={<Home />} />
                  <Route path="/video" element={<Video />} />
                  <Route path="/video/:id" element={<EditarVideo />} />
                  <Route path="/categoria" element={<Categoria />} />
                  <Route path="/categoria/:id" element={<EditarCategoria />} />
                  <Route path="*" element={<Error404 />} />
              </Routes>
              <PieDePagina />
          </Router>
      </ThemeProvider>
  );
}





