import { useParams } from 'react-router-dom';
import React from 'react';
import videos from '/db.json'; // Ajusta la ruta según la ubicación de tu archivo JSON


function Player() {
    const { id } = useParams();
    const video = videos.find(video => video.id === id);


    // Aquí puedes usar el id para buscar el video correspondiente
    // Por ejemplo, podrías tener una lista de videos y buscar el video por id

    return (
        <div>
            <h1>Reproduciendo video {id.titulo}</h1>
            {/* Aquí puedes agregar el reproductor de video */}
            <video controls>
                <source src={`/path/to/videos/${id}.mp4`} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
        </div>
    );
}

export default Player;
