        <Header>
             <HeaderContenido>
                <HeaderLink to='/'>
                    <Logo src={logo} alt="" />
                </HeaderLink>
                {url.pathname === '/' && <BotonLink tipo='lineas' color="#fff" to='/video' >Nuevo Video</BotonLink>}
            </HeaderContenido>
        </Header>
    );
}

export default Header;
