import React from 'react';
import styled from 'styled-components';

const Header = styled.div`
    background: rgb(0, 0, 0);
    text-align: center;
    padding: 1rem 2rem;
    color: rgb(71, 205, 74);
    font-size: 1rem;
    text-align: left;
    border: 1px solid transparent; /* Añadido el tipo de borde */
`;

const Button = styled.button`
    background: rgb(9, 56, 10);
    color: white;
    border: none;
    padding: 0.5rem 1rem;
    margin: 0 0.5rem;
    cursor: pointer;
    border-radius: 5px;
    font-size: 1rem;

    &:hover {
        background: rgb(50, 180, 50);
    }
`;

const AppHeader = () => (
    <Header>
        <h1>Mi Aplicación</h1>
        <Button>Botón 1</Button>
        <Button>Botón 2</Button>
    </Header>
);

export default AppHeader;
